INSERT INTO `article_manage`(`id`, `title`, `img_url`, `content`, `create_time`, `update_time`, `is_use`, `summary`, `style_type`) VALUES (7, '春季清洗保养活动正在进行', 'http://xiaoshanlongxin.com/admin/images/20200318/20200318090506.png', '<p>疫情无情、人有情肺炎病毒给我们生活，工作带来了极大的不便。复工开始了你办公室的空调经过冬季的使用，也吸收的大量的细菌和灰尘。为了感恩回馈新老客户的厚爱和支持，公司对萧山范围内办公、居家中央空调清洗活动现在开始所有清洗按8折收费，需要的朋友可以联系82584680或82173359进行咨询。活动还在进行中，每天前五名打进电话要求清洗的家庭用户享受6折优惠。</p>', '2020-03-18 09:07:32', NULL, 0, '本次折扣时间至2020年6月1日结束每天前5名线上报修享受6折优惠', 2);
INSERT INTO `article_manage`(`id`, `title`, `img_url`, `content`, `create_time`, `update_time`, `is_use`, `summary`, `style_type`) VALUES (8, '公司简介', 'http://xiaoshanlongxin.com/admin/images/20200328/20200328170756.png', '<h2><span style=\"font-weight: bold;\">杭州萧山隆鑫家电成立于2014年,又于2018年成立了杭州佳徳暖通工程有限公司主营美的中央空调、美的家用空气能、美的商用中央热水、大金中央空调，.日立中央空调安装、维修清洗保养等业务。公司有专业技术人员4人、空调专业安装人员12人，空调专业维修人员10人。本单位本着顾客第一的服务宗旨，客户满意100分的服务目标，为广大家庭商业中央空调客户的安装、维修、维护提供了专业优质的服务。是杭州美的暖通设备销售有限公司，青岛日立海信中央空调营销股份有限公司授权的专业安装、维修服务中心。2015年成为青岛日立海信中央空调营销股份有限公司授权的指定服务商，2016年成为美的中央空调指定统一安装网点。和杭州萧山多家经销商及代理商提供安装、维护、维修一条龙服务公司真诚期待你的合作，服务电话0571-82584680,0571-82173359服务监督电话1333615932</span></h2>', '2020-03-28 17:27:11', NULL, 0, '杭州萧山隆鑫专业中央空调，中央热水是萧山区美的，日立中央空调指定的专业售后服务网点', 2);
